import React, { useState, useEffect } from 'react';
import './Post.css';
import Avatar from "@material-ui/core/Avatar";
import { datab } from './firebase';
import firebase from 'firebase';

function post( { postId, username, caption, imageUrl } ) {
    const [comments, setComments] = useState([]);
    const [comment, setComment] = useState('');

    useEffect(() => {
        let unsubscribe;
        if(postId){
            unsubscribe = datab
            .collection("posts")
            .doc(postId)
            .collection("comments")
            .orderBy("timestamp","asc")
            .onSnapshot((snapshot) => {
                setComments(snapshot.docs.map((doc) => doc.data()));
            });
        }
        return () => {
            unsubscribe();
        };
    }, [postId]);
    
    return (
    
    <div className ="Post">
        { /* this part is the header of the user and username*/ }

        <div className ="Post_header">
            <Avatar
                className="Post_Avatar"
                alt='ChristianeTayar'
                src="/static/images/avatar/1.jpg"
            />
            <h3> {username} </h3>
        </div>

            {/* insert picture*/}
            <img className="Post_pic" 
            src={imageUrl}
            alt="" 
            />

            {/* underneath the image -> username and caption*/}
            <h4 className= "Post_text" ><strong> {username} </strong> {caption} </h4>

            {/*comment section over here */}
            <form>
               <input className="Post_input" type="text" placeholder="Add COMMENTS!" value={comment} onChange={(e) => setComment(e.target.value)} /> 
            </form>
    </div>
    )
}

export default post
